SELECT 'ALTER TABLE '+ini.name+' ADD '+icol.name+' '+type_name(icol.system_type_id)+(CASE WHEN type_name(icol.system_type_id) = 'decimal' and icol.max_length = 9 THEN '(15,2)' WHEN type_name(icol.system_type_id) in ('datetime','bit','int','smallint','money','smallmoney') THEN '' WHEN type_name(icol.system_type_id) in ('varchar','varbinary') THEN '(max)' ELSE '('+CONVERT(VARCHAR(4),icol.max_length)+')' END)+' null;' FROM <<ORIGEN>>.sys.Tables as ini INNER JOIN <<ORIGEN>>.sys.Columns as icol On ini.object_id=icol.object_id Where icol.name = <<OBJETO>>;